//unsigned long MWopen(char *devname, unsigned long flag);long MWclose(unsigned long fd);long MWlseek(unsigned long fd, long offset, long flag);long MWread(unsigned long fd, void *buf, long n);long MWwrite(unsigned long fd, void *buf, long n);//////////////////////////////////////////////////////////////////////////////// Bonus file I/O functions!//////////////////////////////////////////////////////////////////////////////long MWbload(char *devname, void *address);//////////////////////////////////////////////////////////////////////////////// Nifty debug/no debug toggling macros///////////////////////////////////////////////////////////////////////////////*	These macros can be used to switch back and forth between the PS-X/OS	file I/O functions and the Metrowerks debug functions.  By defining the	symbol DEBUG_IO in your source (or the PSX_prefix.h file), you can write	your code to use the OS functions and they will be replaced by the	Metrowerks one.  Just un-define the symbol before the final build.*/#ifdef DEBUG_IO#define	open(n,f)		MWopen((n), (f))#define	close(d)		MWclose((d))#define	lseek(d, o, f)	MWlseek((d), (o), (f))#define	read(d, b, n)	MWread((d), (b), (n))#define	write(d, b, n)	MWwrite((d), (b), (n))#define	bload(n, a)		MWbload((n), (a))#endif